import {useState} from 'react';
import './ExpenseForm.css';

const ExpenseForm = (props) => {
	const [enteredTitle, setEnteredTitle] = useState(''),
	[enteredAmount, setEnteredAmount] = useState(''),
	[enteredDate, setEnteredDate] = useState(''),
	[isAdding, setAdding] = useState('false');

	const titleChangeHandler = (e) => {
		setEnteredTitle(e.target.value);
	}, amountChangeHandler = (e) => {
		setEnteredAmount(e.target.value);
	}, dateChangeHandler = (e) => {
		setEnteredDate(e.target.value);
	}, submitHandler = (e) => {
		e.preventDefault();

	

		const expenseData = {
			title: enteredTitle,
			amount: enteredAmount,
			date: new Date(enteredDate),
		};

		props.onSaveExpenseData(expenseData);

		setEnteredTitle('');
		setEnteredAmount('');
		setEnteredDate('');
		
	}

	let formContent;

	// isAdding === 'false' ? formContent = <button>Add new expense</button> : formContent = <form><button>add</button></form>

	return (
		{formContent}
	);
};

export default ExpenseForm;
