import { useContext } from "react";
import DUMMY_DATA from "../../assets/dummy-meals";
import classes from "./Cart.module.css";
import Modal from "../UI/Modal/Modal";
import CartItem from "./CartItem";
import OrderContext from "../../store/order-context";

const Cart = () => {
	let total = 0;
	const cartCtx = useContext(OrderContext);

	const cartItems = cartCtx.cart.map((item) => (
		<CartItem
			key={item.id}
			price={item.price}
			name={item.name}
			amount={item.amount}
		/>
	));

	const totalAmount = cartCtx.cart.forEach((item) => {
		total += item.price * item.amount;
	});

	return (
		<>
			<Modal>
				<ul className={classes["cart-items"]}>{cartItems}</ul>{" "}
				<div className={classes.total}>
					<p>Total amount</p>
					<p>{`$${total.toFixed(2)}`}</p>
				</div>
				<div className={classes.actions}>
					<button type="button" className={classes["button--alt"]}>
						Close
					</button>
					<button type="submit" className={classes.button}>
						Order
					</button>
				</div>
			</Modal>
		</>
	);
};

export default Cart;
