import { useContext, useState } from "react";
import classes from "./Header.module.css";
import mainImage from "../../../assets/meals.jpg";
import HeaderCartButton from "./HeaderCartButton";
import OrderContext from "../../../store/order-context";

const Header = () => {
	const [cartVisibility, setCartVisibility] = useState(false);
	const mealOrderCtx = useContext(OrderContext);
	const openCartHandler = async () => {
		setCartVisibility(true);
		await console.log(cartVisibility);
	};

	return (
		<>
			<header className={classes.header}>
				<h1>ReactMeals</h1>
				<HeaderCartButton onClick={openCartHandler} />
			</header>
			<div className={classes["main-image"]}>
				<img src={mainImage} alt="A table full of delicious food" />
			</div>
		</>
	);
};

export default Header;
