import { createContext, useState } from "react";

const OrderContext = createContext({
	mealID: "id",
	amount: 3,
	setA: () => {},
});

export const OrderContextProvider = () => {
	const handleA = (e) => {
		console.log(e);
	};

	return (
		<OrderContext.Provider
			value={{
				setA: handleA,
			}}></OrderContext.Provider>
	);
};

export default OrderContext;
