import styles from './ErrorModal.module.css';
import Card from './reusable-blocks/Card';

const ErrorModal = () => {
    return <div>
        <div className={styles.error}>
            <p>Invalid input</p>
        </div>
        <Card>
            <p className={styles["error-msg"]}>Please enter a valid age</p>
        </Card>
    </div>
}

export default ErrorModal;
